import React from "react";
import { connect } from "react-redux";
import axios from "axios";
import Link from "next/link";
import Carrito from "./carrito"



class FileProduct extends React.Component {
  static async getInitialProps({ req, query }) {
    let product;
    if (req) {
      product = req.params.product
    } else {
      product = query.product
    }

    const response = await axios.get("https://api.mercadolibre.com/items/MLA773274530")
    console.log(response)
    return {
      product
    }
  }



  render() {
    return (
      <div>
        <p>hola soy file </p>
        <p>{this.props.product}</p>

      <Link href="/carrito" as="carrito"><button>comprar</button></Link>


      </div>
    )
  }
}

export default connect()(FileProduct)
